// src/pages/UserPanel.js
import React from 'react';
import '../assets/UserPanel.css';

const mockZaklady = [
  { id: 1, nazwa: 'Mecz 1', stawka: 50, status: 'Wygrany' },
  { id: 2, nazwa: 'Mecz 2', stawka: 20, status: 'Przegrany' },
];

const mockTransakcje = [
  { id: 1, typ: 'Wp≈Çata', wartosc: 100, data: '2025-05-18 12:00' },
  { id: 2, typ: 'Wyp≈Çata', wartosc: 50, data: '2025-05-19 09:30' },
];

const UserPanel = () => {
  const username = 'JanKowalski';
  const saldo = 130;

  return (
    <div className="container mt-5 pt-5">
      <div className="custom-card p-4">
        <h1 className="mb-4 text-center">Witaj, {username}!</h1>
        <h2 className="mb-3 text-center">üí∞ Saldo: {saldo} z≈Ç</h2>

        <div className="d-flex flex-column align-items-center mb-4 gap-3">
          <a href="/deposit" className="btn btn-danger w-100">üí∏ Wp≈Çata</a>
          <a href="/withdraw" className="btn btn-danger w-100">üèß Wyp≈Çata</a>
        </div>

        <h3>üéØ Twoje zak≈Çady:</h3>
        <ul className="list-group mb-4">
          {mockZaklady.length > 0 ? (
            mockZaklady.map(z => (
              <li key={z.id} className="list-group-item bg-transparent text-dark border-light" style={{ backgroundColor: 'rgba(0,0,0,0.4)' }}>
                <strong>{z.nazwa}</strong><br />
                Stawka: {z.stawka} z≈Ç<br />
                Status: {z.status}
              </li>
            ))
          ) : (
            <li className="list-group-item bg-transparent text-dark border-light">Nie masz ≈ºadnych zak≈Çad√≥w.</li>
          )}
        </ul>

        <h3>üìú Twoje transakcje:</h3>
        <ul className="list-group">
          {mockTransakcje.length > 0 ? (
            mockTransakcje.map(t => (
              <li key={t.id} className="list-group-item bg-transparent text-dark border-light" style={{ backgroundColor: 'rgba(0,0,0,0.4)' }}>
                {t.typ} ‚Äì Kwota: {t.wartosc} z≈Ç ‚Äì Data: {t.data}
              </li>
            ))
          ) : (
            <li className="list-group-item bg-transparent text-dark border-light">Brak transakcji.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default UserPanel;
